package foundation.icon.icx;

import com.fasterxml.jackson.core.JsonProcessingException;
import com.fasterxml.jackson.databind.ObjectMapper;
import com.fasterxml.jackson.databind.module.SimpleModule;
import foundation.icon.icx.data.NetworkId;
import foundation.icon.icx.transport.jsonrpc.RpcItem;
import foundation.icon.icx.transport.jsonrpc.RpcObject;
import foundation.icon.icx.transport.jsonrpc.RpcValue;
import foundation.icon.icx.transport.jsonrpc.Serializers;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.web3j.utils.Numeric;

import java.math.BigInteger;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.mockito.Mockito.mock;

class SignedTransactionTest {
    private ObjectMapper mapper;

    @BeforeEach
    void setUp() {
        mapper = new ObjectMapper();
        SimpleModule module = new SimpleModule();
        module.addSerializer(RpcItem.class, new Serializers.RpcItemSerializer(true));
        mapper.registerModule(module);
    }

    @Test
    void testIcxSerialize() {

//        For the transaction below:
//        {
//            "jsonrpc": "2.0",
//                "method": "icx_sendTransaction",
//                "id": 1234,
//                "params": {
//                    "version": "0x3",
//                    "from": "hxbe258ceb872e08851f1f59694dac2558708ece11",
//                    "to": "hx5bfdb090f43a808005ffc27c25b213145e80b7cd",
//                    "value": "0xde0b6b3a7640000",
//                    "stepLimit": "0x12345",
//                    "nid": "0x1",
//                    "timestamp": "0x563a6cf330136",
//                    "nonce": "0x1",
//                    "signature": "VAia7YZ2Ji6igKWzjR2YsGa2m53nKPrfK7uXYW78QLE+ATehAVZPC40szvAiA6NEU5gCYB4c4qaQzqDh2ugcHgA="
//              }
//        }
//
//        Expected output is:
//        icx_sendTransaction.from.hxbe258ceb872e08851f1f59694dac2558708ece11.nid.0x1.nonce.0x1.stepLimit.0x12345.timestamp.0x563a6cf330136.to.hx5bfdb090f43a808005ffc27c25b213145e80b7cd.value.0xde0b6b3a7640000.version.0x3

        Transaction transaction = TransactionBuilder.of(NetworkId.MAIN)
                .from("hxbe258ceb872e08851f1f59694dac2558708ece11")
                .to("hx5bfdb090f43a808005ffc27c25b213145e80b7cd")
                .value(new BigInteger("de0b6b3a7640000", 16))
                .stepLimit(new BigInteger("12345", 16))
                .timestamp(new BigInteger("563a6cf330136", 16))
                .nonce(new BigInteger("1"))
                .build();
        Wallet wallet = mock(Wallet.class);
        SignedTransaction signedTransaction = new SignedTransaction(transaction, wallet);
        RpcObject properties = signedTransaction.getTransactionProperties();
        String serialize = signedTransaction.serialize(properties);
        assertEquals("icx_sendTransaction.from.hxbe258ceb872e08851f1f59694dac2558708ece11.nid.0x1.nonce.0x1.stepLimit.0x12345.timestamp.0x563a6cf330136.to.hx5bfdb090f43a808005ffc27c25b213145e80b7cd.value.0xde0b6b3a7640000.version.0x3",
                serialize);
    }

    @Test
    void testCallTransactionSerialize() {

        RpcObject params = new RpcObject.Builder()
                .put("_to", new RpcValue("hx5bfdb090f43a808005ffc27c25b213145e80b7cd"))
                .put("_value", new RpcValue(new BigInteger("1")))
                .build();

        Transaction transaction = TransactionBuilder.of(NetworkId.MAIN)
                .from("hxbe258ceb872e08851f1f59694dac2558708ece11")
                .to("cx982aed605b065b50a2a639c1ea5710ef5a0501a9")
                .value(BigInteger.ONE)
                .stepLimit(new BigInteger("75000"))
                .timestamp(new BigInteger("5727e42882650", 16))
                .nonce(new BigInteger("1"))
                .call("transfer")
                .params(params)
                .build();


        Wallet wallet = mock(Wallet.class);
        SignedTransaction signedTransaction = new SignedTransaction(transaction, wallet);
        RpcObject properties = signedTransaction.getTransactionProperties();
        String serialize = signedTransaction.serialize(properties);
        assertEquals("icx_sendTransaction.data.{method.transfer.params.{_to.hx5bfdb090f43a808005ffc27c25b213145e80b7cd._value.0x1}}.dataType.call.from.hxbe258ceb872e08851f1f59694dac2558708ece11.nid.0x1.nonce.0x1.stepLimit.0x124f8.timestamp.0x5727e42882650.to.cx982aed605b065b50a2a639c1ea5710ef5a0501a9.value.0x1.version.0x3",
                serialize);
    }

    @Test
    void testDeployTransactionSerialize() {

        String content = "";

        RpcObject params = new RpcObject.Builder()
                .put("initialSupply", new RpcValue(new BigInteger("10000")))
                .put("decimals", new RpcValue(new BigInteger("18")))
                .put("name", new RpcValue("ICON"))
                .put("symbol", new RpcValue("ICX"))
                .build();

        Transaction transaction = TransactionBuilder.of(NetworkId.MAIN)
                .from("hxbe258ceb872e08851f1f59694dac2558708ece11")
                .to("cx0000000000000000000000000000000000000000")
                .stepLimit(new BigInteger("e01348", 16))
                .timestamp(new BigInteger("5727e42882650", 16))
                .nonce(new BigInteger("1"))
                .deploy("application/zip", Numeric.hexStringToByteArray(content))
                .params(params)
                .build();


        Wallet wallet = mock(Wallet.class);
        SignedTransaction signedTransaction = new SignedTransaction(transaction, wallet);
        RpcObject properties = signedTransaction.getTransactionProperties();
        String serialize = signedTransaction.serialize(properties);
        assertEquals("icx_sendTransaction.data.{content..contentType.application/zip.params.{decimals.0x12.initialSupply.0x2710.name.ICON.symbol.ICX}}.dataType.deploy.from.hxbe258ceb872e08851f1f59694dac2558708ece11.nid.0x1.nonce.0x1.stepLimit.0xe01348.timestamp.0x5727e42882650.to.cx0000000000000000000000000000000000000000.version.0x3",
                serialize);
    }

    @Test
    void testMessageTransactionSerialize() {

        Transaction transaction = TransactionBuilder.of(NetworkId.MAIN)
                .from("hxbe258ceb872e08851f1f59694dac2558708ece11")
                .to("hx5bfdb090f43a808005ffc27c25b213145e80b7cd")
                .stepLimit(new BigInteger("e01348", 16))
                .timestamp(new BigInteger("5727e42882650", 16))
                .nonce(new BigInteger("1"))
                .message("Hello World")
                .build();


        Wallet wallet = mock(Wallet.class);
        SignedTransaction signedTransaction = new SignedTransaction(transaction, wallet);
        RpcObject properties = signedTransaction.getTransactionProperties();
        String serialize = signedTransaction.serialize(properties);
        assertEquals("icx_sendTransaction.data.0x48656c6c6f20576f726c64.dataType.message.from.hxbe258ceb872e08851f1f59694dac2558708ece11.nid.0x1.nonce.0x1.stepLimit.0xe01348.timestamp.0x5727e42882650.to.hx5bfdb090f43a808005ffc27c25b213145e80b7cd.version.0x3",
                serialize);
    }

    @Test
    void testEscapeString() throws JsonProcessingException {
        String temp = "\\.{}[]\"\b한글";
        RpcItem escapeValue = new RpcValue(temp);
        String json = mapper.writeValueAsString(escapeValue);
        assertEquals("\\\\\\.\\{\\}\\[\\]\"\b한글", json);
    }


}
